import React, { useState, createRef, useRef } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
// import Canvas from '../dash/canvas'
import CanvasDraw from 'react-canvas-draw'

const ModalExample = (props) => {

    const {
        buttonLabel,
        className
    } = props;

    const saveableCanvas = useRef();


    const [modal, setModal] = useState(false);

    const toggle = () => setModal(!modal);

    return (
        <div>
            <Button color="primary" size='lg' className='btn-msg' onClick={toggle}>{buttonLabel}</Button>
            <Modal isOpen={modal} toggle={toggle} className={className}>
                <ModalHeader toggle={toggle}>Spiral Drawing Test</ModalHeader>
                <ModalBody>

                    {/* <canvas id="sig-canvas" width="400" height="400">Browser not compatibile.</canvas> */}
                    {/* <CanvasDraw 
                        className='mx-auto sig-canvas'
                        lazyRadius='0'
                        brushRadius='2'
                        brushColor="#222222"
                        hideGrid='true'

                    /> */}

                    <CanvasDraw
                        className='mx-auto sig-canvas'
                        lazyRadius='0'
                        brushRadius='2'
                        brushColor="#222222"
                        hideGrid='true'
                        // ref={(elem) => (console.log(elem))}
                        saveData={localStorage.getItem("savedDrawing")}

                        ref={saveableCanvas}

                    />
                </ModalBody>
                <ModalFooter>

                    <Button
                        color="primary"
                        onClick = {() => {console.log(saveableCanvas.getSaveData())} }
                    >Save</Button>{' '}
                    <Button color="secondary" onClick={toggle}>Cancel</Button>
                </ModalFooter>
            </Modal>
        </div >
    );
}

export default ModalExample;
