import React, { useState, createRef } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
// import Canvas from '../dash/canvas'
import CanvasDraw from 'react-canvas-draw'

const ModalExample = (props) => {
    const {
        buttonLabel,
        className
    } = props;

    const ref = createRef();


    const [modal, setModal] = useState(false);

    const toggle = () => setModal(!modal);

    return (
        <div>
            <Button color="primary" size='lg' className='btn-msg' onClick={toggle}>{buttonLabel}</Button>
            <Modal isOpen={modal} toggle={toggle} className={className}>
                <ModalHeader toggle={toggle}>Spiral Drawing Test</ModalHeader>
                <ModalBody>

                    {/* <canvas id="sig-canvas" width="400" height="400">Browser not compatibile.</canvas> */}
                    {/* <CanvasDraw 
                        className='mx-auto sig-canvas'
                        lazyRadius='0'
                        brushRadius='2'
                        brushColor="#222222"
                        hideGrid='true'

                    /> */}

                    <CanvasDraw
                        ref={canvasDraw => (this.saveableCanvas = canvasDraw)}

                    />
                    <p>
                        The following is a disabled canvas with a hidden grid that we use to
                        load show your saved drawing.
        </p>
                    <button
                        onClick={() => {
                            this.loadableCanvas.loadSaveData(
                                localStorage.getItem("savedDrawing")
                            );
                        }}
                    >
                        Load what you saved previously into the following canvas. Either by
                        calling `loadSaveData()` on the component's reference or passing it
                        the `saveData` prop:
        </button>
                </ModalBody>
                <ModalFooter>

                    <Button
                        color="primary"
                    >Save</Button>{' '}
                    <Button color="secondary" onClick={toggle}>Cancel</Button>
                </ModalFooter>
            </Modal>
        </div >
    );
}

export default ModalExample;
