import React, { useState, createRef, useRef } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
// import Canvas from '../dash/canvas'
import CanvasDraw from 'react-canvas-draw'
import spiral from '../../assets/img/tremor/Spiral_image.jpg'


class ModalTest extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            modal: false,
            results: false
        };
        this.toggle = this.toggle.bind(this);
    }

    toggle() {
        this.setState({
            modal: !this.state.modal
        });
    }

    makeblob(dataURL) {
        var BASE64_MARKER = ';base64,';
        if (dataURL.indexOf(BASE64_MARKER) == -1) {
            var parts = dataURL.split(',');
            var contentType = parts[0].split(':')[1];
            var raw = decodeURIComponent(parts[1]);
            return new Blob([raw], { type: contentType });
        }
        var parts = dataURL.split(BASE64_MARKER);
        var contentType = parts[0].split(':')[1];
        var raw = window.atob(parts[1]);
        var rawLength = raw.length;

        var uInt8Array = new Uint8Array(rawLength);

        for (var i = 0; i < rawLength; ++i) {
            uInt8Array[i] = raw.charCodeAt(i);
        }

        return new Blob([uInt8Array], { type: contentType });
    };

    pingPlatform(img) {
        let url = "https://westus2.api.cognitive.microsoft.com/customvision/v3.0/Prediction/6fede207-de99-4fbe-8f04-44a2154495ad/classify/iterations/Iteration8/image";
        let imgBlob = this.makeblob(img);
        // const req = new Request()
        fetch(url, {
            headers: {
                "Prediction-Key": "78a3f4d1ae95492680685c14da50480d",
                "Content-Type": "application/octet-stream",
                "Prediction-key": "78a3f4d1ae95492680685c14da50480d"
            },
            method: 'POST',
            // processData: false,
            body: imgBlob
        })
            .then(function (response) {
                return response.json();
            })
            .then(function (data) {
                // populateResult(data, img);
                this.populateResult(data, img);
            })
            .catch(function (error) {
                console.log('err');
            });
    }

    populateResult(data, img) {
        let parkisonsPercentage;
        let healthyPercentage;
        if (data.predictions[0].tagName === 'parkinson') {
            parkisonsPercentage = data.predictions[0].probability;
            healthyPercentage = data.predictions[1].probability;
        } else {
            healthyPercentage = data.predictions[0].probability;
            parkisonsPercentage = data.predictions[1].probability;
        }
        let text = 'Our model shows that your spiral is'
        if (healthyPercentage <= parkisonsPercentage) {
            text += `${(parkisonsPercentage * 100).toFixed(1)}% similar to known Parkinson's spirals.`;
        } else {
            text += `${(healthyPercentage * 100).toFixed(1)}% similar to known healthy spirals.`;
        }

        document.getElementById('spiral-cont').style.display = 'none';
        let parent = document.getElementById('spiral-res');

        let div = document.createElement('div');
        div.innerHTML = text;
        div.classList.add('result');
        parent.appendChild(div);

        let desc = document.createElement('div');
        desc.innerHTML = 'These results will be logged and sent to your doctor you may try again. Otherwise you may download your spiral or share directly with your Doctor.';
        desc.classList.add('res-desc');
        parent.appendChild(desc);

        // let down = document.createElement('div');
        // down.innerHTML = 'Download';
        // down.classList.add('dl-spiral');
        // parent.appendChild(down);

        let smallImg = document.createElement('img');
        smallImg.src = img;
        smallImg.classList.add('res-img');
        parent.appendChild(smallImg);

        // remove old buttons
        document.getElementById('upload').style.display = 'none';
        document.getElementById('clear').style.display = 'none';
        // show new buttons
        document.getElementById('retry').style.display = 'flex';
        document.getElementById('finish').style.display = 'flex';
    }

    render() {
        let content;
        if (this.state.results) {



        } else {
            content = <><ModalBody>
                <CanvasDraw
                    className='mx-auto sig-canvas'
                    lazyRadius='0'
                    brushRadius='2'
                    brushColor="#222222"
                    hideGrid='true'
                    hideInterface='true'
                    // imgSrc= 'url(../../assets/img/tremor/Spiral_image.jpg)'
                    // saveData={localStorage.getItem("savedDrawing")}
                    // ref={saveableCanvas}
                    ref={(CanvasReact) => this.saveableCanvas = CanvasReact}
                />
            </ModalBody>
                <ModalFooter>
                    <Button
                        color="secondary"
                        onClick={() => { this.saveableCanvas.clear() }}
                    >Clear</Button>{' '}
                    <Button
                        color="primary"
                        onClick={() => { this.pingPlatform(this.saveableCanvas.canvasContainer.children[1].toDataURL()) }}
                    >
                        Evaluate
        </Button>
                </ModalFooter>
            </>
        }


        return (
            <div>
                <Button color="primary" size='lg' className='btn-msg' onClick={this.toggle}>{this.props.buttonLabel}</Button>
                <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
                    <ModalHeader toggle={this.toggle}>Spiral Drawing Test</ModalHeader>
                    {content}
                </Modal>
            </div>
        );
    }
}

export default ModalTest;
